INSERT INTO series_peliculas VALUES (1, "Stranger things", "Después de la extraña desaparición de un niño, un pueblo se encuentra ante un misterio que revela experimentos secretos, fuerzas sobrenaturales y a una niña muy especial.", "The Duffer Brothers", "Eleven, Mike, Will, Dustin, Lucas, Hopper, Joyce, Nancy, Jonathan, Steve", 2, 2016, 9.7), (2, "Breaking bad", "Un profesor de química de escuela secundaria recurre a la venta de drogas para mantener a su familia.", "Vince Gilligan", "Walter White, Jesse Pinkman, Gus Fring, Saul Goodman, Mike Ehrmantraut, Hank Schrader, Tuco Salamanca, Skyler White", 5, 2008, 7),  (3, "IT", "Un grupo de chicos intimidados se unen cuando un monstruo con apariencia de payaso comienza a cazar niños.", "Stephen King", "El payaso Pennywise, Beverly Marsh, Richie Tozier, Bill Denbrough, Eddie Kaspbrak, Stanley Uris, Ben Hanscom, Mike Hanlon, Georgie Denbrough", null, 2017, 4.9), (4, "Better call Saul", "James Jimmy McGuill, antes de asumir la identidad de Saul Goodman, es un abogado corrupto con un humor políticamente incorrecto vinculado al mundo criminal que empieza a crear una importante red de contactos en los bajos mundos.", "Vince Gilligan, Peter Gould", "Jimmy McGuill, Mike Ehrmantraut, Gus Fring, Hector Salamanca, Tuco Salamanca, Chuck McGill, Kim Wexler, Howard Hamlin, Nacho Varga", 3, 2015, 6);
INSERT INTO series_peliculas (id_contenido, titulo, descripcion, creador, principales, temporadas, estreno) VALUES (5, "The Flash", "Después de ser alcanzado por un rayo, Barry Allen se despierta del coma para descubrir que se le ha dado el poder de la súper velocidad, convirtiéndose en Flash, luchando contra el crimen en Central City.", "Greg Berlanti, Geoff Johns, Andrew Kreisberg", "Barry Allen, Killer Frost, Iris West, Eobard Thawne, Vibe, Wally West, Henry Allen", 3, 2014, 4.7);